import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { 
  faUser, 
  faEnvelope, 
  faLock, 
  faBuilding,
  faPhone,
  faEye,
  faEyeSlash,
  faCheck,
  faTimes,
  faInfoCircle,
  faCheckCircle
} from '@fortawesome/free-solid-svg-icons';
import './AuthLayout.css';

const Register = () => {
  const [formData, setFormData] = useState({
    firstName: '',
    lastName: '',
    email: '',
    company: '',
    phone: '',
    password: '',
    confirmPassword: '',
    acceptTerms: false
  });
  const [errors, setErrors] = useState({});
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [showPassword, setShowPassword] = useState(false);
  const [showConfirmPassword, setShowConfirmPassword] = useState(false);
  const [passwordStrength, setPasswordStrength] = useState(0);
  const [passwordRequirements, setPasswordRequirements] = useState({
    minLength: false,
    hasNumber: false,
    hasSpecialChar: false,
    hasUppercase: false,
    hasLowercase: false,
    noSpaces: true
  });

  const navigate = useNavigate();

  useEffect(() => {
    document.title = 'Create an Account | JSP Distributor';
  }, []);

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: type === 'checkbox' ? checked : value
    }));

    // Clear error when user types
    if (errors[name]) {
      setErrors(prev => ({
        ...prev,
        [name]: ''
      }));
    }

    // Validate on the fly
    if (name === 'password') {
      checkPasswordStrength(value);
    }
  };

  const checkPasswordStrength = (password) => {
    const requirements = {
      minLength: password.length >= 8,
      hasNumber: /\d/.test(password),
      hasUppercase: /[A-Z]/.test(password),
      hasLowercase: /[a-z]/.test(password),
      hasSpecialChar: /[!@#$%^&*(),.?\":{}|<>]/.test(password),
      noSpaces: !/\s/.test(password)
    };

    const strength = Object.values(requirements).filter(Boolean).length;
    setPasswordStrength(Math.round((strength / 6) * 100));
    setPasswordRequirements(requirements);
  };

  const validate = () => {
    const newErrors = {};

    if (!formData.firstName.trim()) newErrors.firstName = 'First name is required';
    if (!formData.lastName.trim()) newErrors.lastName = 'Last name is required';
    if (!formData.email) newErrors.email = 'Email is required';
    else if (!/\S+@\S+\.\S+/.test(formData.email)) newErrors.email = 'Email is invalid';

    if (!formData.company) newErrors.company = 'Company name is required';
    if (!formData.phone) newErrors.phone = 'Phone number is required';

    if (!formData.password) newErrors.password = 'Password is required';
    else if (formData.password.length < 8) newErrors.password = 'Password must be at least 8 characters';

    if (!formData.confirmPassword) newErrors.confirmPassword = 'Please confirm your password';
    else if (formData.password !== formData.confirmPassword) {
      newErrors.confirmPassword = 'Passwords do not match';
    }

    if (!formData.acceptTerms) newErrors.acceptTerms = 'You must accept the terms and conditions';

    return newErrors;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const formErrors = validate();

    if (Object.keys(formErrors).length > 0) {
      setErrors(formErrors);
      return;
    }

    setLoading(true);
    setError('');

    try {
      const response = await fetch('http://localhost:5000/api/auth/register', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        credentials: 'include', // Important for cookies
        body: JSON.stringify({
          firstName: formData.firstName.trim(),
          lastName: formData.lastName.trim(),
          email: formData.email.trim().toLowerCase(),
          company: formData.company.trim(),
          phone: formData.phone.trim(),
          password: formData.password,
          confirmPassword: formData.confirmPassword,
          acceptTerms: formData.acceptTerms
        })
      });

      const data = await response.json();

      if (!response.ok) {
        // Handle validation errors from server
        if (data.message) {
          throw new Error(data.message);
        } else {
          throw new Error('Registration failed. Please try again.');
        }
      }

      // Registration successful
      navigate('/login', { 
        state: { 
          registrationSuccess: true,
          registeredEmail: formData.email
        } 
      });

    } catch (err) {
      setError(err.message || 'Registration failed. Please try again.');
      console.error('Registration error:', err);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="auth-page">
      <div className="auth-container">
        {/* Left Side - Hero Section */}
        <div className="auth-hero">
          <div className="auth-logo">
            <FontAwesomeIcon icon={faCheckCircle} />
            <span>JSP Distributor</span>
          </div>
          <div className="auth-hero-content">
            <h1>Join JSP Distributor</h1>
            <p>Create an account to access our powerful inventory management platform and streamline your distribution process.</p>
          </div>
        </div>

        {/* Right Side - Register Form */}
        <div className="auth-form-container">
          <div className="auth-form-wrapper">
            <div className="auth-card fade-in">
              <div className="auth-header text-center mb-4">
                <h2 className="fw-bold">Create Your Account</h2>
                <p className="text-muted">Join JSP Distributor today</p>
                <div className="divider my-4">
                  <span>or sign up with email</span>
                </div>
              </div>

              {error && (
                <div className="alert alert-danger alert-dismissible fade show" role="alert">
                  {error}
                  <button 
                    type="button" 
                    className="btn-close" 
                    onClick={() => setError('')}
                    aria-label="Close"
                  ></button>
                </div>
              )}

              <form onSubmit={handleSubmit} className="auth-form">
                <div className="row g-3">
                  <div className="col-md-6">
                    <div className="form-group">
                      <label htmlFor="firstName" className="form-label">First Name</label>
                      <div className="input-group">
                        <span className="input-group-text">
                          <FontAwesomeIcon icon={faUser} />
                        </span>
                        <input
                          type="text"
                          className={`form-control ${errors.firstName ? 'is-invalid' : ''}`}
                          id="firstName"
                          name="firstName"
                          value={formData.firstName}
                          onChange={handleChange}
                          required
                          placeholder="John"
                        />
                      </div>
                      {errors.firstName && (
                        <div className="invalid-feedback d-block">
                          {errors.firstName}
                        </div>
                      )}
                    </div>
                  </div>
                  <div className="col-md-6">
                    <div className="form-group">
                      <label htmlFor="lastName" className="form-label">Last Name</label>
                      <div className="input-group">
                        <span className="input-group-text">
                          <FontAwesomeIcon icon={faUser} />
                        </span>
                        <input
                          type="text"
                          className={`form-control ${errors.lastName ? 'is-invalid' : ''}`}
                          id="lastName"
                          name="lastName"
                          value={formData.lastName}
                          onChange={handleChange}
                          required
                          placeholder="Doe"
                        />
                      </div>
                      {errors.lastName && (
                        <div className="invalid-feedback d-block">
                          {errors.lastName}
                        </div>
                      )}
                    </div>
                  </div>
                </div>

                <div className="form-group">
                  <label htmlFor="email" className="form-label">Email Address</label>
                  <div className="input-group">
                    <span className="input-group-text">
                      <FontAwesomeIcon icon={faEnvelope} />
                    </span>
                    <input
                      type="email"
                      className={`form-control ${errors.email ? 'is-invalid' : ''}`}
                      id="email"
                      name="email"
                      value={formData.email}
                      onChange={handleChange}
                      required
                      placeholder="your@email.com"
                    />
                  </div>
                  {errors.email && (
                    <div className="invalid-feedback d-block">
                      {errors.email}
                    </div>
                  )}
                </div>

                <div className="row g-3">
                  <div className="col-md-6">
                    <div className="form-group">
                      <label htmlFor="company" className="form-label">Company</label>
                      <div className="input-group">
                        <span className="input-group-text">
                          <FontAwesomeIcon icon={faBuilding} />
                        </span>
                        <input
                          type="text"
                          className={`form-control ${errors.company ? 'is-invalid' : ''}`}
                          id="company"
                          name="company"
                          value={formData.company}
                          onChange={handleChange}
                          required
                          placeholder="Company Name"
                        />
                      </div>
                      {errors.company && (
                        <div className="invalid-feedback d-block">
                          {errors.company}
                        </div>
                      )}
                    </div>
                  </div>
                  <div className="col-md-6">
                    <div className="form-group">
                      <label htmlFor="phone" className="form-label">Phone Number</label>
                      <div className="input-group">
                        <span className="input-group-text">
                          <FontAwesomeIcon icon={faPhone} />
                        </span>
                        <input
                          type="tel"
                          className={`form-control ${errors.phone ? 'is-invalid' : ''}`}
                          id="phone"
                          name="phone"
                          value={formData.phone}
                          onChange={handleChange}
                          placeholder="(123) 456-7890"
                        />
                      </div>
                      {errors.phone && (
                        <div className="invalid-feedback d-block">
                          {errors.phone}
                        </div>
                      )}
                    </div>
                  </div>
                </div>

                <div className="form-group mb-4">
                  <div className="d-flex justify-content-between align-items-center mb-2">
                    <label htmlFor="password" className="form-label mb-0">Password</label>
                    <button 
                      type="button" 
                      className="btn btn-link p-0 text-decoration-none"
                      onClick={() => setShowPassword(!showPassword)}
                    >
                      <small>
                        <FontAwesomeIcon icon={showPassword ? faEyeSlash : faEye} className="me-1" />
                        {showPassword ? 'Hide' : 'Show'}
                      </small>
                    </button>
                  </div>
                  <div className="input-group">
                    <input
                      type={showPassword ? 'text' : 'password'}
                      className={`form-control form-control-lg ${errors.password ? 'is-invalid' : ''}`}
                      id="password"
                      name="password"
                      value={formData.password}
                      onChange={handleChange}
                      required
                      placeholder="Create a strong password"
                    />
                  </div>

                  {/* Password Strength Meter */}
                  {formData.password && (
                    <div className="mt-3">
                      <div className="d-flex justify-content-between mb-1">
                        <small className="text-muted">Password Strength</small>
                        <small className={`fw-medium ${
                          passwordStrength < 30 ? 'text-danger' : 
                          passwordStrength < 70 ? 'text-warning' : 'text-success'
                        }`}>
                          {passwordStrength < 30 ? 'Weak' :
                          passwordStrength < 70 ? 'Good' : 'Strong'}
                        </small>
                      </div>
                      <div className="progress" style={{ height: '6px', borderRadius: '3px' }}>
                        <div 
                          className={`progress-bar ${
                            passwordStrength < 30 ? 'bg-danger' : 
                            passwordStrength < 70 ? 'bg-warning' : 'bg-success'
                          }`} 
                          role="progressbar" 
                          style={{ width: `${passwordStrength}%` }}
                          aria-valuenow={passwordStrength} 
                          aria-valuemin="0" 
                          aria-valuemax="100"
                        ></div>
                      </div>
                    </div>
                  )}

                  {/* Password Requirements */}
                  <div className="mt-3">
                    <div className="alert alert-light p-2 small">
                      <p className="mb-2 text-muted">
                        <FontAwesomeIcon icon={faInfoCircle} className="me-1" />
                        Your password must contain:
                      </p>
                      <ul className="list-unstyled mb-0">
                        <li className="mb-1">
                          <FontAwesomeIcon 
                            icon={passwordRequirements.minLength ? faCheck : faTimes} 
                            className={`me-2 ${passwordRequirements.minLength ? 'text-success' : 'text-muted'}`}
                          />
                          <span className={passwordRequirements.minLength ? 'text-dark' : 'text-muted'}>
                            At least 8 characters
                          </span>
                        </li>
                        <li className="mb-1">
                          <FontAwesomeIcon 
                            icon={passwordRequirements.hasNumber ? faCheck : faTimes}
                            className={`me-2 ${passwordRequirements.hasNumber ? 'text-success' : 'text-muted'}`}
                          />
                          <span className={passwordRequirements.hasNumber ? 'text-dark' : 'text-muted'}>
                            At least one number
                          </span>
                        </li>
                        <li className="mb-1">
                          <FontAwesomeIcon 
                            icon={passwordRequirements.hasUppercase ? faCheck : faTimes}
                            className={`me-2 ${passwordRequirements.hasUppercase ? 'text-success' : 'text-muted'}`}
                          />
                          <span className={passwordRequirements.hasUppercase ? 'text-dark' : 'text-muted'}>
                            At least one uppercase letter
                          </span>
                        </li>
                        <li className="mb-1">
                          <FontAwesomeIcon 
                            icon={passwordRequirements.hasSpecialChar ? faCheck : faTimes}
                            className={`me-2 ${passwordRequirements.hasSpecialChar ? 'text-success' : 'text-muted'}`}
                          />
                          <span className={passwordRequirements.hasSpecialChar ? 'text-dark' : 'text-muted'}>
                            At least one special character
                          </span>
                        </li>
                      </ul>
                    </div>
                  </div>
                </div>

                <div className="form-group">
                  <label htmlFor="confirmPassword" className="form-label">Confirm Password</label>
                  <div className="input-group">
                    <span className="input-group-text">
                      <FontAwesomeIcon icon={faLock} />
                    </span>
                    <input
                      type={showConfirmPassword ? 'text' : 'password'}
                      className={`form-control ${errors.confirmPassword ? 'is-invalid' : ''}`}
                      id="confirmPassword"
                      name="confirmPassword"
                      value={formData.confirmPassword}
                      onChange={handleChange}
                      required
                      placeholder=""
                    />
                    <button 
                      type="button" 
                      className="btn btn-outline-secondary"
                      onClick={() => setShowConfirmPassword(!showConfirmPassword)}
                    >
                      <FontAwesomeIcon icon={showConfirmPassword ? faEyeSlash : faEye} />
                    </button>
                  </div>
                  {errors.confirmPassword && (
                    <div className="invalid-feedback d-block">
                      {errors.confirmPassword}
                    </div>
                  )}
                </div>

                <div className={`form-check mb-4 ${errors.acceptTerms ? 'is-invalid' : ''}`}>
                  <div className="d-flex">
                    <div className="flex-shrink-0">
                      <input
                        type="checkbox"
                        className={`form-check-input mt-1 ${errors.acceptTerms ? 'is-invalid' : ''}`}
                        id="acceptTerms"
                        name="acceptTerms"
                        checked={formData.acceptTerms}
                        onChange={handleChange}
                        required
                      />
                    </div>
                    <label className="form-check-label small ms-2" htmlFor="acceptTerms">
                      I agree to the <Link to="/terms" className="text-primary text-decoration-underline">Terms of Service</Link> and{' '}
                      <Link to="/privacy" className="text-primary text-decoration-underline">Privacy Policy</Link>
                    </label>
                  </div>
                  {errors.acceptTerms && (
                    <div className="text-danger small mt-1">
                      {errors.acceptTerms}
                    </div>
                  )}
                </div>

                <button 
                  type="submit" 
                  className="btn btn-primary btn-lg w-100 py-3 fw-bold"
                  disabled={loading}
                >
                  {loading ? (
                    <>
                      <span className="spinner-border spinner-border-sm me-2" role="status" aria-hidden="true"></span>
                      Creating Account...
                    </>
                  ) : (
                    <>
                      Get Started
                      <FontAwesomeIcon icon={faCheck} className="ms-2" />
                    </>
                  )}
                </button>

                <div className="text-center mt-4 pt-3 border-top">
                  <p className="mb-0">
                    Already have an account?{' '}
                    <Link to="/login" className="text-primary fw-medium text-decoration-underline">
                      Sign in here
                    </Link>
                  </p>
                </div>
              </form>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Register;
